import axios from "axios";

const URL_API = "http://localhost:5555/api/prompt";

export const getPrompt = async (message) => {
    try {
        const response = await axios.get(`${URL_API}?message=${message}`);
        return response.data;
    } catch (error) {
        console.error(error);
    }
}
